describe HavesController do

  # This should return the minimal set of attributes required to create a valid
  # Have. As you add validations to Have, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all haves as @haves" do
      have = Have.create! valid_attributes
      get :index
      assigns(:haves).should eq([have])
    end
  end

  describe "GET show" do
    it "assigns the requested have as @have" do
      have = Have.create! valid_attributes
      get :show, :id => have.id.to_s
      assigns(:have).should eq(have)
    end
  end

  describe "GET new" do
    it "assigns a new have as @have" do
      get :new
      assigns(:have).should be_a_new(Have)
    end
  end

  describe "GET edit" do
    it "assigns the requested have as @have" do
      have = Have.create! valid_attributes
      get :edit, :id => have.id.to_s
      assigns(:have).should eq(have)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Have" do
        expect {
          post :create, :have => valid_attributes
        }.to change(Have, :count).by(1)
      end

      it "assigns a newly created have as @have" do
        post :create, :have => valid_attributes
        assigns(:have).should be_a(Have)
        assigns(:have).should be_persisted
      end

      it "redirects to the created have" do
        post :create, :have => valid_attributes
        response.should redirect_to(Have.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved have as @have" do
        # Trigger the behavior that occurs when invalid params are submitted
        Have.any_instance.stub(:save).and_return(false)
        post :create, :have => {}
        assigns(:have).should be_a_new(Have)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Have.any_instance.stub(:save).and_return(false)
        post :create, :have => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested have" do
        have = Have.create! valid_attributes
        # Assuming there are no other haves in the database, this
        # specifies that the Have created on the previous line
        # receives the :update_attributes message with whatever params are
        # submitted in the request.
        Have.any_instance.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => have.id, :have => {'these' => 'params'}
      end

      it "assigns the requested have as @have" do
        have = Have.create! valid_attributes
        put :update, :id => have.id, :have => valid_attributes
        assigns(:have).should eq(have)
