        Have.any_instance.stub(:save).and_return(false)
        put :update, :id => have.id.to_s, :have => {}
        assigns(:have).should eq(have)
      end

      it "re-renders the 'edit' template" do
        have = Have.create! valid_attributes
        # Trigger the behavior that occurs when invalid params are submitted
        Have.any_instance.stub(:save).and_return(false)
        put :update, :id => have.id.to_s, :have => {}
        response.should render_template("edit")
